  }

  return (
    <div >
      <Navbar/>
      <ExtentionList
      AllBtn={showAll}
      ActiveBtn={showActive}
      InactiveBtn={showInactive}
      />
      <div className='extentionCards'>
      {data.map((item)=>(
        <ExtentionCard
        key={item.id}
        logo={item.logo}
        name={item.name}
        description={item.description}
        isActive={item.isActive}
        onToggle={()=> handleToggle(item.id)}
        onRemove={()=> handleRemove(item.id)}
        />
      ))}

    </div>

    </div>
  );
}

export default App;
